import http from "./axiosHttp";

const getAll = () => {
    return http.get("/posts");
};
const removepost = id => {
    console.log(id);
    return http.delete(`/posts/${id}`);
};
const createpost = data => {
    return http.post("/posts", data);
};
export default {
    getAll,
    removepost,
    createpost,
    
};
