import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory, useParams } from 'react-router-dom'

const EditProducts= ({ products, onEdit }) => {
    let history = useHistory();
    const { id } = useParams();
    const onSubmit = (event) => {
        event.preventDefault();
        const { name, image, price, value } = event.target;
        const params = {
            id: id,
            name: name.value,
            image: image.value,
            price: price.value,
        }
        onEdit(id, params);
        history.push("/admin/products");
    }
    return (
        <div>
            {products.map((product) => {
                if (product.id == id) {
                    return (              
                        <div >
                            <div className="content-wrapper">
                             <div className="row">
             <div className="col-md"></div>
             <div className="col-md-10">
                                <h1 className="card-title">Sửa sản phẩm</h1>
                                <form onSubmit={onSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                                        <input type="text"
                                            name="name"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.name} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                                        <input type="number"
                                            name="price"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.price} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                                        <input type="text"
                                            name="image"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.image} />
                                    </div>
                         <button type="submit" className="btn btn-primary">Submit</button>
                                </form>
                                </div>
             <div className="col-md"></div>
           </div>
           </div>
                        </div>
                    );
                }
            })}


        </div>
    )
}

EditProducts.propTypes = {

}

export default EditProducts
