import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useHistory} from "react-router-dom"
import { useForm } from 'react-hook-form'
import Swal from 'sweetalert2'
const AddCategories = ({onAddCate}) => {
  let history = useHistory();
    const [ValueInput, setValueInput] = useState({});

    const onHandleChange = (e) =>{
        const {name_cate ,desc, value} = e.target;
        setValueInput({
            ...ValueInput,
            [name_cate]: e.target.value,
            [desc]: e.target.value
        });
    }
    const onSubmit = (e) => {
        onAddCate(ValueInput)
        history.push("/admin/categories")
    }
    const { register, handleSubmit, watch, errors } = useForm();
    return (
       <div>
         <div className="content-wrapper">
           <div className="row">
             <div className="col-md"></div>
             <div className="col-md-10">
             <h1 classname="h3 mb-2 text-gray-800">Thêm Danh Mục</h1>
      <form>
        <div className="form-group">
          <label htmlFor="inputCategoryName">Tên Danh Mục</label>
          <input name = "name_cate" type="text" className="form-control"
           id="inputCategoryName" onChange = {onHandleChange} 
           ref={register({ required: true, minLength: 5 })}/>
           {errors.name_cate && <span style={{ color: "red" }}>Tên danh mục không được để trống, không dưới năm ký tự</span>}
        </div>
        <div className="form-group">
          <label htmlFor="inputCategoryDesc">Mô Tả</label>
          <input name = "desc" type="text" className="form-control" 
          id="inputCategoryDesc" onChange = {onHandleChange} 
          ref={register({ required: true })}/>
          {errors.desc && <span style={{ color: "red" }}>Mô tả danh mục không được để trống</span>}
        </div>
        <button onClick={handleSubmit(onSubmit)} type="submit" className="btn btn-primary">Submit</button>
      </form>
             </div>
             <div className="col-md"></div>
           </div>
    </div>
    </div>

    )
}

AddCategories.propTypes = {

}

export default AddCategories
